import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Home from './components/Home.js';
import SoilQuality from './components/SoilQuality.js';
import BestPractices from './components/BestPractices.js';
import GovernmentSchemes from './components/GovernmentSchemes.js';
import About from './components/About.js';
import Login from './components/Login.js';
import Register from './components/Register.js';
import Navbar from './components/Navbar.js';
import authHandler from './utils/authHandler';

function App() {
  const [activeTab, setActiveTab] = useState('home');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Initialize auth handler
    const initAuth = async () => {
      const authStatus = await authHandler.loadAuthStatus();
      setIsLoggedIn(authStatus.authenticated);
    };
    initAuth();
  }, []);

  return (
    <Router>
      <div className="App">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} isLoggedIn={isLoggedIn} />

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/home" element={<Home />} />
          <Route path="/soil" element={<SoilQuality />} />
          <Route path="/practices" element={<BestPractices />} />
          <Route path="/schemes" element={<GovernmentSchemes />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/register" element={<Register setIsLoggedIn={setIsLoggedIn} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
